#| label: log_Like_GEV

log_Like_GEV <- function(par,Q){
  n <- length(Q)
  csi <- par[1]
  alpha <- par[2]
  kappa <- par[3]

  y <- 1 - kappa/alpha*(Q - csi);
  term2 = (1/kappa - 1)*log(y) - y^(1/kappa);

  if (kappa < 0) {
    if (min(Q) < csi + alpha/kappa){
      likelihood <- 1e6
      } else {
        likelihood <- -(-n*log(alpha) + sum(term2)) 
      }
    }
  if (kappa > 0) {
    if (max(Q) > csi + alpha/kappa){
      likelihood <- 1e6
    } else {
        likelihood <- -(-n*log(alpha) + sum(term2))
    }
  }
  return(likelihood)
}
```
