  summarise(min = min(n),
            Q10 = floor(quantile(n,probs = 0.10)),
            Q25 = floor(quantile(n,probs = 0.25)),
            median = quantile(n,probs = 0.50),
            Q75 = floor(quantile(n,probs = 0.75)),
            Q90 = floor(quantile(n,probs = 0.90)),
            max = max(n),
            media = mean(n))
