  geom_line(data = n_stations_year,aes(Ano,n),colour = 'black', linewidth = 1) +
   labs(title = "Chuva Máxima Diária no Brasil", subtitle = "Número de estações",
       caption = "fonte dos dados: ANA (2022)",
       x = "Datas", y = "Estações") +
  theme_bw()
